from typing import List, Tuple

class Solution:
    def fractionalKnapsack(self, items: List[Tuple[int, int]], capacity: int) -> float:
        # Sort items by value-to-weight ratio in descending order
        items.sort(key=lambda x: x[0] / x[1], reverse=True)

        total_value = 0.0
        remaining_capacity = capacity

        for item_value, item_weight in items:
            if remaining_capacity == 0:
                break
            elif item_weight <= remaining_capacity:
                # Add the entire item
                total_value += item_value
                remaining_capacity -= item_weight
            else:
                # Add a fraction of the item
                total_value += item_value * (remaining_capacity / item_weight)
                remaining_capacity = 0

        return total_value
