class Solution:
    def fullJustify(self, words: List[str], maxWidth: int) -> List[str]:
        def justify_line(words, width):
            if len(words) == 1 or width == len(''.join(words)):
                return ' '.join(words) + ' ' * (maxWidth - width)
            else:
                spaces = maxWidth - sum(len(word) for word in words)
                gaps = len(words) - 1
                base, extra = divmod(spaces, gaps)
                return (' ' * (base + 1)).join(words[:extra]) + ' ' * (extra + 1) + (' ' * base).join(words[extra:])

        result = []
        current_line = []
        current_length = 0

        for word in words:
            if current_length + len(word) + len(current_line) > maxWidth:
                result.append(justify_line(current_line, current_length))
                current_line = []
                current_length = 0
            current_line.append(word)
            current_length += len(word)

        # Justify the last line
        result.append(' '.join(current_line).ljust(maxWidth))

        return result
