from typing import List

class Solution:
    def fullJustify(self, words: List[str], maxWidth: int) -> List[str]:
        result = []
        current_line = []
        current_width = 0

        for word in words:
            if current_width + len(word) + len(current_line) > maxWidth:
                # Distribute spaces evenly
                if len(current_line) == 1:
                    result.append(current_line[0] + ' ' * (maxWidth - current_width))
                else:
                    total_spaces = maxWidth - current_width
                    space_between_words = total_spaces // (len(current_line) - 1)
                    extra_spaces = total_spaces % (len(current_line) - 1)

                    justified_line = ''
                    for i, w in enumerate(current_line):
                        justified_line += w
                        if i < len(current_line) - 1:
                            justified_line += ' ' * space_between_words
                            if i < extra_spaces:
                                justified_line += ' '
                    result.append(justified_line)

                # Reset current line
                current_line = [word]
                current_width = len(word)
            else:
                current_line.append(word)
                current_width += len(word)

        # Handle last line
        last_line = ' '.join(current_line)
        if len(last_line) < maxWidth:
            last_line += ' ' * (maxWidth - len(last_line))
        result.append(last_line)

        return result
