from typing import List, Tuple, Dict, Optional, Set, Any
import heapq
import collections

Item = Tuple[int, int]

def fractionalKnapsack(items: List[Item], capacity: int) -> Tuple[int, int]:
    # Sort items by value-to-weight ratio in descending order
    sorted_items = sorted(items, key=lambda item: -item[0]/item[1])

    max_value = 0
    total_weight = 0
    for value, weight in sorted_items:
        if capacity >= weight:
            # If the knapsack can hold the whole item, add it entirely
            max_value += value
            total_eight = weight
            capacity -= weight
        else:
            # Otherwise, take a fraction of the item to fill up the remaining space in the knapsack
            fraction = capacity / weight
            max_value += value * fraction
            total_weight += weight * fraction
            break  # No more items can be added

    return (max_value, total_weight)

class Solution:
    def fractionalKnapsack(self, items: List[Item], capacity: int) -> Tuple[int, int]:
        return fractionalKnapsack(items, capacity)
