class Solution:
    def fractional_knapsack(self, items: List[Item], capacity: int) -> float:
        # Sort items by value-to-weight ratio in descending order
        items.sort(reverse=True)

        total_value = 0.0
        for item in items:
            if capacity >= item.weight:
