class Solution:
    def ladderLength(self, beginWord: str, endWord: str, wordList: List[str]) -> int:
        # Preprocess the word list to create a set for quick lookup
        word_set = set(wordList)

        if endWord not in word_set:
            return 0

        # Initialize a queue for BFS and a dictionary to track visited words
        queue = collections.deque([(beginWord, 1)])
        visited = {beginWord: True}

        while queue:
            current_word, level = queue.popleft()

            # If the current word is the end word, return the level (number of steps)
            if current_word == endWord:
                return level

            # Generate all possible one-letter variations of the current word
            for i in range(len(current_word)):
                for char in 'abcdefghijklmnopqrstuvwxyz':
                    next_word = current_word[:i] + char + current_word[i+1:]

                    # If the next word is in the word set and not visited, add it to the queue
                    if next_word in word_set and next_word not in visited:
                        visited[next_word] = True
                        queue.append((next_word, level + 1))

        # If no path is found, return 0
        return 0

