from typing import Tuple, Optional
import heapq

class Solution:
    def find_min_cost(self, start: Tuple[int, int], target: Tuple[int, int]) -> int:
        directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
        rows, cols = 101, 101
        min_cost = [[float('inf')] * cols for _ in range(rows)]
        min_cost[start[0]][start[1]] = 0
        pq = [(0, start[0], start[1])]

        while pq:
            current_cost, x, y = heapq.heappop(pq)

            if (x, y) == target:
                return current_cost

            for dx, dy in directions:
                nx, ny = x + dx, y + dy
                if 0 <= nx < rows and 0 <= ny < cols:
                    new_cost = current_cost + 1  # Assuming cost is always positive and equal to 1 for simplicity
                    if new_cost < min_cost[nx][ny]:
                        min_cost[nx][ny] = new_cost
                        heapq.heappush(pq, (new_cost, nx, ny))

        return float('inf')
