from typing import List

class Solution:
    def matrixChainOrder(self, p: List[int]) -> int:
        n = len(p)
        dp = [[0 for _ in range(n)] for _ in range(n)]

        # cost is zero when multiplying one matrix.
        for i in range(1, n):
            dp[i][i] = 0

        # L is chain length
        for L in range(2, n):
            for i in range(1, n - L + 1):
                j = i + L - 1
                dp[i][j] = float('inf')
                for k in range(i, j):
                    q = dp[i][k] + dp[k+1][j] + p[i-1]*p[k]*p[j]
                    if q < dp[i][j]:
                        dp[i][j] = q

        return dp[1][n-1]
