from typing import List

class Solution:
    def fullJustify(self, words: List[str], maxWidth: int) -> List[str]:
        lines = []
        i = 0

        while i < len(words):
            line = [words[i]]
            totalLength = len(words[i])

            i += 1
            while i < len(words) and totalLength + 1 + len(words[i]) <= maxWidth:
                line.append(words[i])
                totalLength += 1 + len(words[i])
                i += 1

            if i == len(words):
                # Last line, left-justify
                line = ' '.join(line)
                line += ' ' * (maxWidth - len(line))
            else:
                # Full justification for other lines
                spaces = maxWidth - totalLength
                gaps = len(line) - 1
                if gaps == 0:
                    line.append(' ' * spaces)
                else:
                    extraSpaces, baseSpaces = divmod(spaces, gaps)
                    for j in range(gaps):
                        line[j] += ' ' * (baseSpaces + (1 if j < extraSpaces else 0))

            lines.append(''.join(line))

        return lines
